<?phpclass searchDeep extends MY_Controller {    public function __construct() {        parent::__construct();        $this->load->model('content_model', 'content');        $this->load->model('service_content_model', 'service_content');        $this->load->model('openness_content_model', 'openness_content');		$this->load->model('site_channel_tree_model', 'site_channel_tree');    }    protected function searchList($keywords = NULL, $from_date, $to_date, $limit = 10, $offset = 0, $length = 60, $date_format = 0, $description_length, $field = null, $type) {        $arr_sort = array('sort' => 'DESC');        $date_format = $this->date_foramt[$date_format];        $select = array('_id', 'title', 'description', 'release_date','body');        if ($type == 'service_content') {            $item_list = $this->service_content->searchList(null, $keywords, array('status' => True, 'removed' => false, 'site_id' => $this->site_id), $from_date, $to_date, $limit, $offset, $select, $arr_sort, $field);        } else {            $item_list = $this->content->findList(null, $keywords, array('status' => True, 'removed' => false, 'site_id' => $this->site_id), $from_date, $to_date, $limit, $offset, $select, $arr_sort, $field);        }        foreach ($item_list as $key => $item) {            $item_list[$key]['_id'] = (string) ($item['_id']);            $item['title'] = strip_tags(html_entity_decode($item['title']));            if (mb_strlen($item['title']) > $length) {                $item_list[$key]['short_title'] = mb_substr($item['title'], 0, $length) . '...';            } else {                $item_list[$key]['short_title'] = $item['title'];            }            $item_list[$key]['short_title'] = str_replace($keywords, '<font color="red">'.$keywords.'</font>', $item_list[$key]['short_title']);            $item['description'] = str_replace("\n", "<br/>", str_replace(Chr(32), "&nbsp;", $item['description']));            if (mb_strlen($item['description']) > $description_length) {                $item_list[$key]['description'] = mb_substr($item['description'], 0, $description_length) . '...';            }            $item_list[$key]['description'] = str_replace($keywords, '<font color="red">'.$keywords.'</font>', $item_list[$key]['description']);            if ($type == 'service_content') {                $item_list[$key]['url'] = !empty($item['link_url']) ? $item['link_url'] : '/service/contentDetail/' . $item['_id'] . '.html';            } else {                $item_list[$key]['url'] = !empty($item['link_url']) ? $item['link_url'] : $this->folder_prefix . '/detail/' . $item['_id'] . '.html';            }            $item_list[$key]['date'] = ($item['release_date']) ? date($date_format, $item['release_date']) : '';        }               return $item_list;    }    protected function searchCount($keywords = NULL, $field = null, $type, $from_date, $to_date) {        if ($type == 'service_content') {            $count = $this->service_content->searchCount(null, $keywords, array('status' => True, 'removed' => false, 'site_id' => $this->site_id), $from_date, $to_date, $field);        } else {            $count = $this->content->listCount(null, $keywords, array('status' => True, 'removed' => false, 'site_id' => $this->site_id), $from_date, $to_date, $field);        }        return $count;    }    protected function getMenu($channel_id, $limit = 50, $offset = 0, $length = 60) {        $result = array();        $channel_tree = $this->site_channel_tree->find(array('_id' => $channel_id), 1);        if (isset($channel_tree['child'])) {            $i = 0;            foreach ($channel_tree['child'] as $key => $value) {                if ($i >= $limit) {                    break;                }                if ($i < $offset) {                    continue;                }                $result[$key] = $value;                $i++;            }        }        return $result;    }    public function index() {        $View = new Blitz('template/search-deep.html');        $data = array();		$data['location']='<a href="/">首页</a> / 高级搜索';        $View->display($data);    }    public function deep() {        $keywords = $this->input->get('keywords');        $page = (int) $this->input->get('page');        if ($page == 0) {            $page = 1;        }        $field = $this->input->get('field') ? (string) $this->input->get('field') : null;        $type = $this->input->get('type') ? (string) $this->input->get('type') : null;        $from_date = $this->input->get('from_date') ? (string) $this->input->get('from_date') : null;        $to_date = $this->input->get('to_date') ? (string) $this->input->get('to_date') : null;        $total_row = $this->searchCount($keywords, $field, $type, $from_date, $to_date);        $View = new Blitz('template/search.html');        $struct_list = $View->getStruct();        $data = array();        foreach ($struct_list as $struct) {            $matches = array();            if (preg_match('@^/([a-z]+)-([\w\-]+)/$@', $struct, $matches)) {                $action = $matches[1];                $struct_val = trim($matches[0], '/');                $item_list = '';                //列表                if ($action == 'search') {                    list($limit, $offset, $length, $date_format, $description_length) = explode('_', $matches[2]);                    if ($offset == 'page') {                        $offset = $limit * ($page - 1);                    }                    $item_list = $this->searchList($keywords, $from_date, $to_date, $limit, $offset, $length, $date_format, $description_length, $field, $type);                } elseif ($action == 'page') {                    $per_count = (int) $matches[2];                    if ($per_count == 0) {                        $per_count = 20;                    }                    $link = $this->getPagination($total_row, $page, $per_count, 0);                    $item_list['page'] = $link;                } elseif ($action == 'menu') {                    list($parent_id, $limit, $offset, $length) = explode('_', $matches[2]);                    if ($parent_id != 'current') {                        $channel_id = $parent_id;                    } else {                        $channel_id = $parent_channel['_id'];                    }                    $menu_list = $this->getMenu($channel_id, $limit, $offset, $length);                    $i = 0;                    foreach ($menu_list as $key => $menu) {                        $item_list[$i]['_id'] = $key;                        $item_list[$i]['url'] = $this->folder_prefix . '/channel/' . $key . '/';                        $item_list[$i]['name'] = $menu;                        $i++;                    }                }                $data[$struct_val] = $item_list;            }        }        $data = array_merge($data, array(            'keywords' => $keywords,            'total_row' => $total_row,            'folder_prefix' => $this->folder_prefix,        	'location'=>'<a href="/">首页</a> / <a href="#">搜索列表</a>'        ));        $View->display($data);    }}?>